mapper.update:
update orders
status =#{status}, user_id =#{userId}, address_book_id =#{addressBookId}, order_time =#{orderTime}, checkout_time =#{checkoutTime}, pay_method =#{payMethod}, pay_status =#{payStatus}, amount =#{amount}, phone =#{phone}, estimated_delivery_time =#{estimatedDeliveryTime}, cancel_reason =#{cancelReason}, address =#{address}, cancel_time =#{cancelTime},
where number = #{number}

mapper.select:
select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}
